SET SQL_MODE = "NO_AUTO_VALUE_ON_ZERO";
SET time_zone = "+00:00";

DROP TABLE IF EXISTS `countries`;
CREATE TABLE IF NOT EXISTS `countries` (
  `code` varchar(2) NOT NULL DEFAULT '',
  `iso_country` varchar(255) NOT NULL DEFAULT '',
  `locale` varchar(255) NOT NULL,
  `country_ar_SA` varchar(255) NOT NULL,
  `country_cs_CZ` varchar(255) NOT NULL,
  `country_de_DE` varchar(255) NOT NULL,
  `country_en_GB` varchar(255) NOT NULL,
  `country_es_ES` varchar(255) NOT NULL,
  `country_fr_FR` varchar(255) NOT NULL,
  `country_it_IT` varchar(255) NOT NULL,
  `country_ja_JP` varchar(255) NOT NULL,
  `country_nl_NL` varchar(255) NOT NULL,
  `country_pt_PT` varchar(255) NOT NULL,
  `country_ru_RU` varchar(255) NOT NULL,
  `country_sk_SK` varchar(255) NOT NULL,
  `country_zh_TW` varchar(255) NOT NULL,
  `country_zh_CN` varchar(255) NOT NULL,
  `lat` float NOT NULL DEFAULT '0',
  `lon` float NOT NULL DEFAULT '0',
  `cont` varchar(2) NOT NULL,
  PRIMARY KEY (`code`),
  KEY `code` (`code`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;




