SET SQL_MODE = "NO_AUTO_VALUE_ON_ZERO";
SET time_zone = "+00:00";

DROP TABLE IF EXISTS `countries`;
CREATE TABLE `countries` (
  `code` varchar(2) CHARACTER SET ascii NOT NULL DEFAULT '',
  `cur_code` varchar(3) CHARACTER SET ascii NOT NULL,
  `flag` varchar(10) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL,
  `longname` varchar(255) CHARACTER SET utf8mb4 NOT NULL,
  `name_ar` varchar(255) CHARACTER SET utf8mb4 NOT NULL,
  `name_cs` varchar(255) CHARACTER SET utf8mb4 NOT NULL,
  `name_cy` varchar(255) CHARACTER SET utf8mb4 NOT NULL,
  `name_da` varchar(255) CHARACTER SET utf8mb4 NOT NULL,
  `name_de` varchar(255) CHARACTER SET utf8mb4 NOT NULL,
  `name_en` varchar(255) CHARACTER SET utf8mb4 NOT NULL,
  `name_es` varchar(255) CHARACTER SET utf8mb4 NOT NULL,
  `name_fr` varchar(255) CHARACTER SET utf8mb4 NOT NULL,
  `name_he` varchar(255) CHARACTER SET utf8mb4 NOT NULL,
  `name_it` varchar(255) CHARACTER SET utf8mb4 NOT NULL,
  `name_ja` varchar(255) CHARACTER SET utf8mb4 NOT NULL,
  `name_nl` varchar(255) CHARACTER SET utf8mb4 NOT NULL,
  `name_pt` varchar(255) CHARACTER SET utf8mb4 NOT NULL,
  `name_ru` varchar(255) CHARACTER SET utf8mb4 NOT NULL,
  `name_sk` varchar(255) CHARACTER SET utf8mb4 NOT NULL,
  `name_zh-cn` varchar(255) CHARACTER SET utf8mb4 NOT NULL,
  `name_zh-hk` varchar(255) CHARACTER SET utf8mb4 NOT NULL,
  `latitude` double NOT NULL DEFAULT '0',
  `longitude` double NOT NULL DEFAULT '0',
  `elevation` float NOT NULL,
  `continent` varchar(2) CHARACTER SET ascii NOT NULL,
  `locale` varchar(512) CHARACTER SET ascii NOT NULL,
  `dialcode` varchar(9) CHARACTER SET ascii NOT NULL,
  `area` float NOT NULL,
  `population` int(11) NOT NULL,
  `place_id` varchar(64) CHARACTER SET ascii NOT NULL
) ENGINE=MyISAM DEFAULT CHARSET=utf8;



ALTER TABLE `countries`
  ADD PRIMARY KEY (`code`),
  ADD KEY `code` (`code`);
